                price: 10,
                productBy: "facebook"
            }
        };
        this.setProduct = this.setProduct.bind(this)
      }
    
    makePayment = token => {
      const body = {
        token, 
        product
      }
      const headers = {
        "Content-Type": "application/json"
      }
      return fetch(`http://localhost:5000/payment`, {
        method: "POST",
        headers,
        body: JSON.stringify(body)
      }).then(res => {
        console.log("RESPONSE")
