import React, { Component } from 'react'
import Solidlotion from './images/Solidlotion.jpg'
import bodyWash from './images/bodyWash.jpg'
import bodyScrub from './images/bodyScrub.jpg'
import FM from './images/FaceMask.jpg'
import { connect } from 'react-redux'
import { addToCart } from '../actions/cartActions'

class ArrivalsCard extends Component {

      
  handleClick = (id)=>{
    this.props.addToCart(id); 
  }

  render(){
 
  let itemList = this.props.items.map(item=>{})   
  return (
    <div id="carouselArrivalIndicators" className="carousel slide carousel-parent" data-ride="carousel">
      <div className="carousel-inner">
        <div className="carousel-item active">
          <div className="row product-row">
              <div className="col-lg-6 col-md-6 col-sm-12">

              </div>
          </div>
        </div>
      </div>
    </div>
  )
  }
}


const mapStateToProps = (state)=>{
  return {
    items: state.items
  }
}
const mapDispatchToProps= (dispatch)=>{
  
  return{
      addToCart: (id)=>{dispatch(addToCart(id))}
  }
}

export default connect(mapStateToProps,mapDispatchToProps)(ArrivalsCard)
